using System; using System.Text.RegularExpressions; using System.Windows.Forms;  namespace MazeGeneratorProject.Forms {     public partial class LoginScreen : Form {         Regex UsernameRegex = new Regex(@"^[a-zA-Z0-9]+$");                  public LoginScreen() {             InitializeComponent();             lbl_title.Font = StyleSheet.Headings;             lbl_usrnm.Font = StyleSheet.Body;             txtbx_username.Font = StyleSheet.Body;             bttn_login.Font = StyleSheet.Body;         }          private void LoginScreen_Load(object sender, EventArgs e) {                      }          private void bttn_login_Click(object sender, EventArgs e) {             if (!UsernameRegex.IsMatch(txtbx_username.Text)) { MessageBox.Show("Enter a username consisting of letters and numbers!"); return; }              User user;             if (!User.ReadUserFromFile(txtbx_username.Text, out user)) {                 if (MessageBox.Show("User not found, create a new user account?", "New User?", MessageBoxButtons.YesNo) == DialogResult.Yes) {                     user = new User(txtbx_username.Text);                     user.SaveToFile();                     Program.appWindow.SetActiveForm(new MainMenu(user));                 }             } else {                  Program.appWindow.SetActiveForm(new MainMenu(user));             }         }      } }